package rikkei.academy.controller;import com.sun.org.apache.xpath.internal.operations.Mod;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;import rikkei.academy.model.Customer;import rikkei.academy.service.CustomerService;import rikkei.academy.service.impl.SimpleCustomerServiceImpl;import java.util.List;@Controller@RequestMapping("CustomerController")public class CustomerController{    @Autowired    private SimpleCustomerServiceImpl simpleCustomerService ;    @RequestMapping(value = "getAll")    public ModelAndView getAllList (){        ModelAndView modelAndView = new ModelAndView("list");        List<Customer> list = simpleCustomerService.findAll();        modelAndView.addObject("list",list);        return modelAndView;    }    @GetMapping("info")    public String info(Model model, long id){        Customer customer = simpleCustomerService.findOne(id);        model.addAttribute("customer", customer);        return "info";    }    @PostMapping("update")    public String updateCustomer(Customer customer){       Customer customer1 = simpleCustomerService.merge(customer);       return "redirect:getAll";    }}