package ra.Controller;import ra.Model.Entity.Catalog;import ra.Model.Entity.Contact;import ra.Model.Entity.ContactEmail;import ra.Model.Service.ContactEmailService;import ra.Model.Service.ContactService;import ra.Model.ServiceImp.ContactEmailServiceImp;import ra.Model.ServiceImp.ContactServiceImp;import javax.servlet.*;import javax.servlet.http.*;import javax.servlet.annotation.*;import java.io.IOException;import java.util.List;@WebServlet(name = "ContactEmailServlet", value = "/ContactEmailServlet")public class ContactEmailServlet extends HttpServlet {    private ContactEmailService<ContactEmail,String> contactServiceImp = new ContactEmailServiceImp();    @Override    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {             getAllProduct(request, response);    }    public void getAllProduct(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        List<ContactEmail> contactList = contactServiceImp.getAll();        request.setAttribute("contactEmail", contactList);        request.getRequestDispatcher("View/Admin/contact.jsp").forward(request,response);    }    @Override    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        request.setCharacterEncoding("UTF-8");        String action = request.getParameter("action");        if (action != null && action.equals("Send Message")) {            ContactEmail c = new ContactEmail();            c.setName(request.getParameter("name"));            c.setEmail(request.getParameter("email"));            c.setSubject(request.getParameter("subject"));            c.setMessage(request.getParameter("message"));            boolean result = contactServiceImp.save(c);            if (result) {                request.getRequestDispatcher("View/User/alertRate.jsp").forward(request, response);            } else {                request.getRequestDispatcher("View/Admin/Error/error.jsp").forward(request, response);            }        }    }}